const ProductCard = ({product}) => {
  const router = useRouter();
   
  const {currency} = useAppContext();
  const { addToCart,  updateCartItem, removeFromCart, cartItems } = useAppContext();

  return product && (
    <TouchableOpacity onPress={() => {router.push(`/products/${product._id}`)}}>
    <View style={styles.card}>
        <View style={styles.imageContainer}>
        <Image
          source={{ uri: product.image[0] }}
          style={styles.image}
          resizeMode="contain"
        />
      </View>


      <View>
        <Text style={styles.category}>{product.category}</Text>
        <Text style={styles.name} numberOfLines={1}>
          {product.name}
        </Text>

        {/* Rating */}
        <View style={styles.ratingContainer}>
          {[...Array(5)].map((_, i) => (
            <FontAwesome
              key={i}
              name={product.rating > i ? "star" : "star-o"}
              size={14}
              color="#615fff"
              style={{ marginRight: 2 }}
            />
          ))}
          <Text style={styles.ratingText}>({product.rating})</Text>
        </View>

        {/* Price & Counter */}
        <View style={styles.priceRow}>
          <Text style={styles.offerPrice}>
           {product.offerPrice} {currency}{" "}
            <Text style={styles.originalPrice}>{product.price}{currency}</Text>
          </Text>

          {/* Counter */}
          <TouchableOpacity onPress={(e) => {e.stopPropagation();}}>
          {!cartItems[product._id] ? (

            <TouchableOpacity
              onPress={() => addToCart(product._id)}
              style={styles.addButton}
              activeOpacity={0.7}
            >
        
               <Ionicons name="cart-outline" size={26} color="#615fff" />
              <Text style={styles.addText}>Add</Text>
            </TouchableOpacity>
          ) : (
            <View style={styles.counter}>
              <TouchableOpacity
                onPress={() => {removeFromCart(product._id)}}
              >
                <Text style={styles.counterButton}>-</Text>
              </TouchableOpacity>
              <Text style={styles.counterValue}>{cartItems[product._id]}</Text>
              <TouchableOpacity
                onPress={() => {addToCart(product._id)}}
              >
                <Text style={styles.counterButton}>+</Text>
              </TouchableOpacity>
            </View>
          )}
          </TouchableOpacity>
        </View>
      </View>
    </View>
    </TouchableOpacity>
  );
};

